mapper.update:
UPDATE goods
SET goodsPicturePath = CONCAT(goodsPicturePath, #{goodsPicturePath})
WHERE goodsId = #{goodsId}

UPDATE goods
SET goodsPictureInfoPath = CONCAT(goodsPictureInfoPath, #{goodsPictureInfoPath})
WHERE goodsId = #{goodsId}

UPDATE goods
SET goodsVideoPath = #{goodsVideoPath}
WHERE goodsId = #{goodsId}

mapper.select:
SELECT goodsPicturePath
FROM goods
WHERE goodsId = #{goodsId}

SELECT goodsPictureInfoPath
FROM goods
WHERE goodsId = #{goodsId}

SELECT goodsVideoPath
FROM goods
WHERE goodsId = #{goodsId}

mapper.update:
UPDATE goods
SET
  goodsStatus = 4,
  goodsModifyTime = #{goodsModifyTime}
WHERE goodsId = #{goodsId}
